    public function deleteBlog($id)
    {
        try
        {
            $q = Doctrine_Query::create()
                ->delete()
                ->delete('Summers_Model_Blog b')
                ->where('b.blogid = ?', $id);
            $q->execute();
        } catch(Exception $e) {
            $this->errorStack()->add($e->getMessage());
        }
    }

