     * @param $id
     *
     * @return array
     */
    public static function getBlog_byId($id)
    {
        $q = Doctrine_Query::create()
            ->from('Summers_Model_Blog b')
            ->where('b.blogid = ?', $id);

        return $q->fetchArray();
    }

