class Summers_Model_Blog extends Summers_Model_BaseBlog
{
    protected $_blogs = array();

    public function deleteBlog($id)
    {
        try
        {
            $q = Doctrine_Query::create()
                ->delete()
                ->delete('Summers_Model_Blog b')
                ->where('b.blogid = ?', $id);
            $q->execute();
        } catch(Exception $e) {
            $this->errorStack()->add($e->getMessage());
        }
    }

    public function setBlog_byId($id)
    {
        $value = $this::getBlog_byId($id);
        if(count($value)==1) {
            $this->fromArray($value[0]);
        } else {
            //add( string $invalidFieldName, string $errorCode = 'general' )
            $this->errorStack->add('tooManyRows');
        }
    }

    public function getBlogs()
    {
        return $this->_blogs;
    }

    public function setBlogs($active = true)
    {
        $q = Doctrine_Query::create()
            ->from('Summers_Model_Blog b')
            ->where('b.blogstatus =?',(($active)?1:0))
            ->orderBy('b.blogupdate desc');

        $this->_blogs = $q->fetchArray();
    }

    function setMask_ToFullArray()
    {
        foreach ($this->_blogs as $key => $value) {
            $this->_blogs[$key]['mask']
                = $this->maskToArray($this->_blogs[$key]['mask']
            );
        }
    }

     /**
     * from str to array
     *
     * @param $mask
     *
     * @return array|bool
     */
    function maskToArray($mask)
    {
        if ($mask) {
            return Summers_Snenko::getArray($mask);
        }
        return $mask;
    }

    // --------------------------------------------------------
    // --- static ---------------------------------------------
    // --------------------------------------------------------

    /**
     * Получаємо блог через id
     * @param $id
     *
     * @return array
     */
    public static function getBlog_byId($id)
    {
        $q = Doctrine_Query::create()
            ->from('Summers_Model_Blog b')
            ->where('b.blogid = ?', $id);

        return $q->fetchArray();
    }

    public function getLastArticles($limit) {
        $idFieldName = Doctrine_Core::getTable('Summers_Model_Blog')->getIdentifier();//blogid

        $q = Doctrine_Query::create()
            ->from('Summers_Model_Blog b')
            ->orderBy('b.'.$idFieldName.' DESC')
            ->limit($limit);
        //rreturn $q->execute(array(), Doctrine_Core::HYDRATE_SCALAR);
        return $q->fetchArray();
    }
}